import React, { useState, useEffect } from 'react';
import api from "./services/api";

import "./global.css";
import "./App.css";
import "./Sidebar.css";
import "./Main.css";

function App() {

	const [github_useraname, setGithubUsername] = useState("");
	const [techs, setTechs] = useState("");
	const [latitude, setLatitude] = useState("");
	const [longitude, setLongitude] = useState("");

	useEffect(() => {
		navigator.geolocation.getCurrentPosition(
			(position) => {
				console.log(position)
				const { latitude, longitude } = position.coords;

				setLatitude(latitude);
				setLongitude(longitude);
			},
			(err) => {
				console.log(err);
			},
			{
				timeout: 30000,
			}
		);
	}, [])

	async function handleAddDev(e) {
		e.preventDefault();

		const response = await api.post("/devs", {
			github_useraname,
			techs,
			la
		});
	}

	return (
		<div id="app">
			<aside>
				<strong>Cadastrar</strong>
				<form onSubmit={handleAddDev}>
					<div className="input-block">
						<label htmlFor="github_username">Usuário do Github</label>
						<input
							name="github_username"
							id="github_username"
							type="text"
							required
							value={github_useraname}
							onChange={e => setGithubUsername(e.target.value)}
						/>
					</div>
					<div className="input-block">
						<label htmlFor="techs">Tecnologias</label>
						<input
							name="techs"
							id="techs"
							type="text"
							required
							value={techs}
							onChange={e => setTechs(e.target.value)}
						/>

					</div>
					<div className="input-group">
						<div className="input-block">
							<label htmlFor="latitude">Latitude</label>
							<input
								type="number"
								name="latitude"
								id="latitude"
								required
								value={latitude}
								onChange={e => setLatitude(e.target.value)}
							/>
						</div>
						<div className="input-block">
							<label htmlFor="longitute">Longitute</label>
							<input
								type="number"
								name="longitute"
								id="longitute"
								required
								value={longitude}
								onChange={e => setLongitude(e.target.value)}
							/>
						</div>
					</div>

					<button type="submit">Salvar</button>
				</form>
			</aside>

			<main>
				<ul>
					<li className="dev-item">
						<header>
							<img src="https://avatars2.githubusercontent.com/u/49207932?s=460&v=4" alt="Matheus Pires" />
							<div className="user-info">
								<strong>Matheus Pires</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Estagiário Front-end na Serafine.</p>
						<a href="https://github.com/MatheusPiress99">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars2.githubusercontent.com/u/49207932?s=460&v=4" alt="Matheus Pires" />
							<div className="user-info">
								<strong>Matheus Pires</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Estagiário Front-end na Serafine.</p>
						<a href="https://github.com/MatheusPiress99">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars2.githubusercontent.com/u/49207932?s=460&v=4" alt="Matheus Pires" />
							<div className="user-info">
								<strong>Matheus Pires</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Estagiário Front-end na Serafine.</p>
						<a href="https://github.com/MatheusPiress99">Acessar perfil no Github</a>
					</li>
					<li className="dev-item">
						<header>
							<img src="https://avatars2.githubusercontent.com/u/49207932?s=460&v=4" alt="Matheus Pires" />
							<div className="user-info">
								<strong>Matheus Pires</strong>
								<span>ReactJS, React Native, Node.js</span>
							</div>
						</header>
						<p>Estagiário Front-end na Serafine.</p>
						<a href="https://github.com/MatheusPiress99">Acessar perfil no Github</a>
					</li>
				</ul>
			</main>
		</div>
	);
}

export default App;
